/** * Class constructor for regResults, all the necessary information for SS type I,II & III * are here. Also, there is an error constructor. */package elvira.tools.statistics.anova;public class regResults {			public double rsq=0.0;			public double esq=0.0;			public double sst=0.0;			public double rsqr=0.0;			public int dfr=0;			public int dfe=0;			public double[] b;			public int err=0;						regResults(double rsq, double sst, double[] b, int dfr, int dfe) {				this.rsq=rsq;				this.sst = sst;				this.esq=sst-rsq;				this.rsqr = rsq/sst;								this.b = b;				this.dfr = dfr;				this.dfe = dfe;			}						regResults(int err) {				this.err=err;			}					}			